package main

import (
	"math/big"
)

const smallestInt = `-`

func compareBigInts(ar []string) interface{} {
	if len(ar) < 2 {
		return -1
	}
	l, sl := "", ""
	l = smallestInt
	sl = smallestInt
	flag := false
	for _, num := range ar {
		n0 := new(big.Int)
		n0, ok := n0.SetString(num, 10)
		if !ok {
			continue
		}

		n1 := new(big.Int)
		n1, ok = n1.SetString(l, 10)
		if !ok {
			continue
		}

		cmp := n0.Cmp(n1)

		if cmp == 1 {
			sl = l
			l = num
			continue
		}

		//

		n2 := new(big.Int)
		n2, ok = n2.SetString(sl, 10)
		if !ok {
			continue
		}

		cmp1 := n0.Cmp(n2)

		//if current number is greater than or equal to second smallest number
		//and not equal to the largest number then assign it to second sl
		if (cmp1 == 1 || cmp1 == 0) && cmp != 0 {
			sl = num
			flag = true
			continue
		}
	}
	if (sl == smallestInt && !flag) || l == sl {
		return -1
	}
	return sl
}
